"use client";
import React, { useState } from 'react';
import Navbar from'./comp/Navbar';
import ReviewCard from'./comp/Reviewcard';
import './Main.css';
import subtitleImg from './img/mainsubtitle.png'
import PermIdentityIcon from '@mui/icons-material/PermIdentity';
import KeyboardDoubleArrowRightIcon from '@mui/icons-material/KeyboardDoubleArrowRight';
import bannerImg from './img/banner1.png'
import reviewtitleImg from './img/reviewtitle.png'
import ReviewerCard from'./comp/ReviewerCard';
import Footer from'./comp/footer';
import product1Image from './img/product1.png';
import product2Image from './img/product_2.png';
import product3Image from './img/product_3.png';

const productDataList =[{
  imageSrc: product1Image,
  productName: 'Î∂ÑÌôçÏù¥/Ï¥àÎ°ùÏù¥ 35ÏùºÎ∂Ñ',
  rating: '4.21',
  reviewCount: 150,
  hashtags: ['#Í∞ÄÎ•¥ÏãúÎãàÏïÑ', '#Ï≤¥ÏßÄÎ∞© Í∞êÏÜå'],
  price: '44,000',
},
{
  imageSrc: product2Image,
  productName: 'Î©îÌÉÄÍ∑∏Î¶∞ Ïä¨Î¶ºÏóÖ',
  rating: '4.13',
  reviewCount: 120,
  hashtags: ['#Ïπ¥ÌÖåÌÇ®', '#Ï≤¥ÏßÄÎ∞© Í∞êÏÜå'],
  price: '44,000',
},
{
  imageSrc: product3Image,
  productName: 'ÏÜçÌé∏Ìïú Îã§Ïù¥Ïñ¥Ìä∏',
  rating: '4,11',
  reviewCount: 115,
  hashtags: ['#Í∞ÄÎ•¥ÏãúÎãàÏïÑ', '#Î∂ÑÎßê'],
  price: '55,000',
},
]

function Search() {
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredProducts, setFilteredProducts] = useState([]);

  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);

    // Filter products based on the search query
    const filtered = productDataList.filter((product) =>
      product.productName.toLowerCase().includes(query.toLowerCase())
    );

    setFilteredProducts(filtered);
  };

  return (
    <div className="container">
      <div className="search-container">
        <form>
          <div className="search-section">
            <input
              type="text"
              id="search"
              className="input"
              placeholder="ÏõêÌïòÎäî Îã§Ïù¥Ïñ¥Ìä∏ Î≥¥Ï°∞Ï†úÎ•º Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî."
              value={searchQuery}
              onChange={handleSearch}
              required
            />

            <button type="submit" className="button">
              Search
            </button>
          </div>
        </form>
      </div>
      {/* Render the filtered products */}
      <div className="card-container" style={{ display: 'flex', gap: '45px' }}>
        {filteredProducts.length > 0
          ? filteredProducts.map((productData, index) => (
              <ReviewCard key={index} productData={productData} />
            ))
          : productDataList.map((productData, index) => (
              <ReviewCard key={index} productData={productData} />
            ))}
      </div>
    </div>
  );
}
  
function Main() {
  
  return (
    <div>
        <div className="main-container">
            <Navbar />
            <Search />
        </div>
        
          <div className="pickdesc">
            <div className="pick-txt">
              <img src={subtitleImg} alt="Main Subtitle" className='subtitleImg'/>
            <div className="subtxt">
                <p>Ïä¨Î¶ºÌÉÄÏûÑÏù¥ Ï∂îÏ≤úÌïòÎäî Îã§Ïù¥Ïñ¥Ìä∏ Î≥¥Ï°∞Ï†ú Top 3Î•º ÎßåÎÇòÎ≥¥ÏÑ∏Ïöî. </p>
                <p>Ïä¨Î¶ºÌÉÄÏù¥Î®∏Îì§Ïù¥ ÎÇ®Í∏¥ ÌèâÏ†ê Î∞è Î¶¨Î∑∞ Í∏∞Î∞òÏúºÎ°ú Ï∂îÏ≤úÌï¥ÎìúÎ¶ΩÎãàÎã§.</p>
            </div>
            </div>
          </div>
            <div className="card-container" style={{ display: 'flex', gap: '45px' }}>
            {productDataList.map((productData, index) => (
          <ReviewCard key={index} productData={productData} />
        ))}
            </div>
      
        <div className="ad-container">
            <div className='challenge-container'>
                <div className='challenge-section'>
                    <p>üî•Ïù∏Í∏∞ Diet Ï±åÎ¶∞ÏßÄÎ∞©</p>
                </div>
                <div className="challengetitle">
                    <p>Îß§Ïùº 1,000kcal ÌÉúÏö∞Í∏∞ Ï±åÎ¶∞ÏßÄ</p>
                </div>
                <div className='challenge-detail'>
                    <div className="count">
                        <PermIdentityIcon></PermIdentityIcon>
                        <p> 100Î™Ö</p>
                    </div>
                    <div className="arrow">
                        <KeyboardDoubleArrowRightIcon></KeyboardDoubleArrowRightIcon>
                    </div>
                </div>
            </div>
            <div className='oliveyoung-container'>
                <img
                width='770'
                height='310'
                src={bannerImg}
                alt="ad"
                />
                <div className="oliveyoung-txt">
                    <p>Ïò¨Î¶¨Î∏åÏòÅ Î∏îÎûôÌîÑÎùºÏù¥Îç∞Ïù¥ 50% ÏÑ∏Ïùº</p>
                    <KeyboardDoubleArrowRightIcon></KeyboardDoubleArrowRightIcon>
                </div>
            </div>
        </div>
        <div className='topreviewers-container'>
            <img src={reviewtitleImg} alt="topreviewers" className='reviewtitleImg'/>
            <div className="reviewercards">
                <ReviewerCard/>
            </div>
        </div>
        <div className='footer-container'>
            <Footer/>
        </div>
    </div>
  );
}

export default Main;
