import React, { useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import {Button} from './Button';
import logoImage from "../img/logo.png";
import './Navbar.css'
import { useNavigate } from 'react-router-dom';

function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const navigate= useNavigate();
  

    const handleClick = () => setClick(!click) ;
    const closeMobileMenu = () => setClick(false);

    // 화면 크기에 따라서 버튼이 보이고 안보이도록 설정한다. 
    const showButton = () => {
        if(window.innerWidth <= 960){
            setButton(false)
        }
        else {
            setButton(true);
        }
    };

    // SIGNUP버튼이 사이즈가 줄어들면 없어지도록 한다. 
    useEffect(() => {
        showButton();
    }, []);


    window.addEventListener('resize', showButton);

    return (
        <>
        <nav className = 'navbar'>
            <div className = 'navbar-container'>
                {/* 모바일버전에서 클릭하면 메뉴 보이도록 설정하는 것도 한다. (close Mobile Menu)는 다시 버튼 누르면 없어지고 생기고 하도록 한다.  */}
                <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
                    <img src={logoImage} alt='Logo' />
                </Link>
                <div className='menu-icon' onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    <li className='nav-item'>
                        <Link to='/search' className='nav-links' onClick = {closeMobileMenu}>
                            Diet 보조제 서치
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/services' className='nav-links' onClick = {closeMobileMenu}>
                            Diet 보조제 리뷰
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/products' className='nav-links' onClick = {closeMobileMenu}>
                            Diet 챌린지
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/products' className='nav-links' onClick = {closeMobileMenu}>
                            Diet  토크
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/login' className='nav-links-mobile'>
                            로그인
                        </Link>
                    </li>
                    <li >
                        <Link to='/sign' className='nav-links-mobile' >
                            회원가입
                        </Link>
                    </li>
                </ul>
                {button && <Button where = "/login" buttonStyle='btn--inline'>로그인</Button>}
                {button && <Button  where = "/sign"buttonStyle='btn--inline' >회원가입</Button>}
            </div>
        </nav>
        </>
    );
}

export default Navbar
